#
# Structure for table "hm_article"
#

DROP TABLE IF EXISTS `hm_article`;
CREATE TABLE `hm_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章表',
  `cate_id` int(11) NOT NULL DEFAULT '0' COMMENT '关联文章分类',
  `title` varchar(20) NOT NULL DEFAULT '' COMMENT '标题',
  `image` varchar(50) NOT NULL DEFAULT '' COMMENT '封面图',
  `description` text COMMENT '描述',
  `content` text COMMENT '内容',
  `addtime` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `is_show` int(11) NOT NULL DEFAULT '1' COMMENT '是否显示 1显示 2隐藏',
  `type` int(11) NOT NULL DEFAULT '1' COMMENT '重要性，1普通，2置顶',
  `sort` int(11) NOT NULL DEFAULT '50',
  PRIMARY KEY (`id`),
  KEY `cate_id` (`cate_id`)
) ENGINE=MyISAM AUTO_INCREMENT=81 DEFAULT CHARSET=utf8;

#
# Data for table "hm_article"
