  `parent_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '文章分类上级ID',
  `name` varchar(20) NOT NULL DEFAULT '',
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=MyISAM AUTO_INCREMENT=65 DEFAULT CHARSET=utf8;

#
# Data for table "hm_article_category"
