#
# Structure for table "hm_doctor"
#

DROP TABLE IF EXISTS `hm_doctor`;
CREATE TABLE `hm_doctor` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `sex` int(11) NOT NULL DEFAULT '0' COMMENT '0是女 1是男',
  `job` varchar(20) NOT NULL DEFAULT '' COMMENT '职称',
  `edu` int(11) NOT NULL DEFAULT '0' COMMENT '1初中 2高中 3本科 4硕士 5博士 6博士后',
  `Room` varchar(20) NOT NULL DEFAULT '' COMMENT '科室',
  `OpRoom` varchar(20) NOT NULL DEFAULT '' COMMENT '门诊科室',
  `OpTime` varchar(60) NOT NULL DEFAULT '' COMMENT '出诊时间',
  `description` text NOT NULL COMMENT '个人简介',
  `name` varchar(20) NOT NULL DEFAULT '' COMMENT '医生姓名',
  `image` varchar(50) NOT NULL DEFAULT '' COMMENT '医生图片',
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

#
# Data for table "hm_doctor"
#

/*!40000 ALTER TABLE `hm_doctor` DISABLE KEYS */;
INSERT INTO `hm_doctor` VALUES (1,1,'教授,研究生导师',5,'精神科','情感障碍门诊','周一上午,周四上午','1985年毕业于华西医科大学，从事精神科临床32年，获中国医师协会精神科医师分会“优秀精神科医师”称号。擅长治疗的症状：擅长诊治各类重性精神障碍、情绪障碍，富有心理治疗经验。参与各学术组织的情况：任广东省医学会精神医学分会副主任委员，广东省医师协会精神科医师协会常委，广东省心理卫生协会副理事长，汕头市医学会精神病学分会主任委员。主持汕头大学医学院《精神病学》、《沟通技能》模块教学，获汕头大学李嘉诚基金会“卓越教师奖”。主持完成国家自然科学基金、广东省自然科学基金各1项。','许崇涛','/uploadimages/0503215740.jpg'),(3,1,'主任医师,教授',5,'精神科 ','儿少精神科门诊','周三上午',' 1983年湖南湘雅医科大学毕业以来，在医学院的附属医院从事精神病学和医学心理学的临床教学和科研工作30多年，擅长抑郁症、焦虑障碍等各种精神疾病的诊断和治疗，擅长心理咨询和心理治疗。目前担任中国心理学会医学心理学专业委员会委员、中国心理卫生协会心理评估专业委员会委员、《国际精神病学杂志》和《中国临床心理学杂志》编委。','刘少文','/uploadimages/0503223843.jpg'),(4,1,'主任医生',5,'精神科','精神科','周二上午，周三上午','从医近30余年，擅长各类重性精神疾病、抑郁症、焦虑症及失眠症等治疗与康复。2006年取得德国法兰克福精神分析研究院颁发的心理治疗师资格，针对心理疾患人群开展心理治疗取得良好的效果。现任广东省心理卫生协会常务理事，广东省心理咨询师专业委员会常委。广东省精神残疾康复专业委员会副主任委员，汕头市精神残疾协会主席。','郑少雄','/uploadimages/0503223947.jpg'),(5,1,'主任医师,心理治疗师',3,'精神科','精神科','周二下午、周四上午','1994年毕业于广东省汕头大学医学院临床医学专业，至今在汕头大学精神卫生中心从事精神科临床、科研及教学工作（长期担任本科生、研究生的临床实习、见习带教工作），从医二十多年，担任病区主任十多年，有丰富的临床经验。擅长于难治性精神分裂症、躁狂症、抑郁症、焦虑症、癫痫性精神障碍、气功所致精神障碍、酒精所致精神障碍、儿童情绪与行为问题、老年性痴呆的治疗和心理咨询及心理治疗。主持广东省卫生厅医学科学技术研究基金等立项四项，研究方向为难治性精神障碍的治疗与临床精神药物的应用。是国家自然科学基金项目精神疾病、神经系统疾病评审专家，是广东省科技厅、江苏省科技厅、四川省科技厅等省的科技评审专家。','肖勃','/uploadimages/0503224716.jpg'),(6,1,'主任医师,医学院兼聘教授',3,'精神科','精神科','周一下午，周三上午','汕大医学院临床医学本科毕业，华西医科大学精神医学研修班结业。从事精神病学与医学心理学的临床、教学、科研工作30年，擅长于精神分裂症、抑郁症、癫痫、儿童行为问题、老年性痴呆等疾病的治疗，对精神疾病和精神创伤的司法鉴定有深入研究。现为汕大精神卫生中心潮州脑科医院执行院长，汕大司法鉴定中心法医精神病学科组组长，司法部法医精神病学鉴定专业司法鉴定人；广东省法医精神病学专业委员会委员、省心理学会理事；汕头市心理学会副理事长、市司法鉴定学会理事、市精神病学专业委员会委员；《中华行为医学与脑科学杂志》通讯编委。承担医学院研究生、本科生和成教生的《精神病学》、《司法精神病学》、《犯罪心理学》和《医学心理学》等课程的教学，培养硕士研究生多名；主持省部、市厅级科研课题多项，发表论文50多篇，多次获得省、市科技进步奖。','朱少毅','/uploadimages/0503224149.jpg'),(7,1,'主任医师',3,'精神科','精神科','周一上午,周二下午','毕业于汕头大学医学院，获本科学士学位。从事精神科临床、教学、科研工作工作20来年，长期工作在临床一线，熟悉临床精神医学及医学心理学，积累了丰富的临床经验；负责大量门诊病人的诊疗工作，组织病房开展疑难病人查房；负责下级医师、研究生及学生的带教工作；擅长心理咨询、失眠、焦虑症、情感障碍（躁狂、忧郁症）、精神分裂症、癫痫及所致精神障碍等疾病的诊断治疗；参加卫生部举行的精神科临床进修班学习1年；主持省市级科研立项3项，发表精神科专业论文十多篇。','卓永宁','/uploadimages/0503224240.jpg'),(8,0,'主任医师,医学硕士',4,'精神科 ','精神科','周一全天,周五全天','1989年汕头大学医学院医疗专业毕业，获医学学士学位。1993年在北京医科大学精神卫生研究所进修。2005年汕头大学医学院精神病与精神卫生专业研究生毕业，获医学硕士学位。临床工作：自1989年大学毕业后一直在汕头大学精神卫生中心工作。擅长各类精神障碍的诊治，特别是失眠、行为异常、心理咨询与妇女精神障碍。科学研究：主要研究抑郁症的发病机理，先后完成汕头市重点科技项目与省卫生厅课题，均为项目负责人。在《中华精神科杂志》与《中国行为医学科学》等国家级刊物发表论文近10篇。','陈纯姿','/uploadimages/0503224330.jpg'),(9,1,'主任医师',3,'精神科','精神科','周三上午','1989年毕业于汕头大学医学院医疗系，从事精神科临床、教学、科研及病房管理工作近30年。擅长对脑外伤所致精神障碍、各类型的精神分裂症、躁狂症、抑郁症、焦虑症、强迫症、睡眠障碍等常见疾病的诊断治疗，对精神科急救、复杂疑难病例的诊断和治疗积累丰富的临床实践经验。曾参加由香港中文大学主办的心理认知行为治疗高级培训班学习，从事心理咨询，开展认知行为（CBT）治疗。主持完成市厅级以上课题3项，在省级以上专业学术期刊发表论文二十多篇。','陈平周','/uploadimages/0503224412.jpg'),(10,0,'主任医师',3,'精神科 ','精神科','周二上午,周五下午','1991年毕业于汕头大学医学院临床医疗专业，从事精神科临床工作26年余，擅长治疗各类精神疾病，如精神分裂症、情感性精神病（躁狂症抑郁症）、精神活性物质所致精神障碍、强迫症、焦虑症、睡眠障碍等。广东省和汕头市心理学会会员，劳动能力鉴定专家组成员，承担临床规培医生的带教和考核，曾主持及完成汕头市科委立项“影响儿童期心理卫生的家庭心理因素研究”，发表论文十余篇。','梁春莲','/uploadimages/0503224501.jpg');
/*!40000 ALTER TABLE `hm_doctor` ENABLE KEYS */;

