DROP TABLE IF EXISTS `hm_systemfoot`;
CREATE TABLE `hm_systemfoot` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `email` varchar(20) NOT NULL DEFAULT '' COMMENT '邮箱地址',
  `zipcode` varchar(20) NOT NULL DEFAULT '' COMMENT '邮编',
  `logo` varchar(50) NOT NULL DEFAULT '' COMMENT '底部logo',
  `phone` varchar(20) NOT NULL DEFAULT '' COMMENT '总机',
  `footstr` varchar(100) NOT NULL DEFAULT '' COMMENT '底部备案号',
