#
# Procedure "getfourdate"
#

DROP PROCEDURE IF EXISTS `getfourdate`;
CREATE PROCEDURE `getfourdate`(

)
BEGIN 
  SELECT count(*) as TodayVisitor
	 ,(SELECT count(DISTINCT ip)  from hm_UserLog where TO_DAYS(time)=TO_DAYS(now())) as TodayUser
	,(SELECT count(*) from hm_UserLog where DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= time) as MouthVisitor
	,(SELECT count(DISTINCT ip) from hm_UserLog where DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= time) as MouthUser
   from hm_UserLog where TO_DAYS(time)=TO_DAYS(now());
 
END;

#
# Procedure "getjson"
#

DROP PROCEDURE IF EXISTS `getjson`;
CREATE PROCEDURE `getjson`(

)
BEGIN 
 SELECT time,count(ip) as amountvisitor,count(distinct ip) as orderuser from hm_UserLog where DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= time GROUP BY time;
 
END;
